import { StyleSheet, SafeAreaView, View, Text } from 'react-native'


const Card = ({ title, amount, date }) => {
	return (
		<SafeAreaView style={styles.note}>
			<View>
				<View sytle={styles.date}>
					<span>{date}</span>
				</View>


				<span>{title}</span>
				<span>{amount}</span>
			</View>
		</SafeAreaView>
	);
};

const styles = StyleSheet.create({
	note: {
		backgroundColor: '#fafafa',
		border: 'solid',
		color: '#9e5cf6',
		borderRadius: 25,
		padding: '1rem',
		minHeight: 260,
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'space-between',
		whiteSpace: 'pre-wrap',
		width: '80%',
		marginLeft: '10%',
		marginTop: '5%',
		marginBottom: 2,
		fontSize: 18,
	},
	date: {
		backgroundColor: '#666',
	}
});

export default Card
