import { useState, useEffect } from 'react'
import { nanoid } from 'nanoid'
import NotesList from './NotesList'
import { StyleSheet, View, Text, SafeAreaView } from 'react-native'


export default function Expenses() {
	const [notes, setNotes] = useState([
		{
			id: nanoid(),
			key: 'Зразок',
			text: 'Тут ключ',
			date: 'Місце для передавання ключа',
			today: '25/10/2023'
		},
		{
			id: nanoid(),
			key: 'Зразок',
			text: 'Тут ключ',
			date: 'Місце для передавання ключа',
			today: '25/10/2023'
		},
	]);

	useEffect(() => {
		const savedNotes = JSON.parse(
			localStorage.getItem('noteslist')
		);

		if (savedNotes) {
			setNotes(savedNotes);
		}
	}, []);

	useEffect(() => {
		localStorage.setItem(
			'noteslist',
			JSON.stringify(notes)
		);
	}, [notes]);

	return (
		<SafeAreaView>
			<View>
				<View style={styles.btns}>
					<View style={styles.middle}>
						<Text style={styles.text}>Додати</Text>
					</View>
				</View>
					<NotesList
						notes={notes}
					/>
			</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	middle: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
	},
	btns: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
		width: '90%',
		marginLeft: '4%',
		marginTop: 20,
	},
	text: {
		fontSize: 20,
		marginTop: -1,
		marginLeft: 1,
	},
	txt: {
		fontSize: 16,
		marginTop: 12,
		marginBottom: 4,
		color: '#888'
	},
	txtdef: {
		fontSize: 16,
		color: '#888'
	},
	middletxt: {
		alignItems: 'center',
	},
});
