import { StyleSheet, SafeAreaView, View, Text } from 'react-native'


const Card = ({ title, amount, date }) => {
	return (
		<SafeAreaView style={styles.note}>
			<View style={styles.centerText}>
				<span>{title}</span>
				<span>{amount}</span>
				<span>{date}</span>
			</View>
		</SafeAreaView>
	);
};

const styles = StyleSheet.create({
	note: {
		backgroundColor: '#fafafa',
		border: 'solid',
		color: '#000',
		borderStartColor: '#9e5cf6',
		borderEndColor: '#9e5cf6',
		borderTopColor: '#9e5cf6',
		borderBottomColor: '#9e5cf6',
		borderRadius: 25,
		padding: '1rem',
		minHeight: 260,
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'space-between',
		whiteSpace: 'pre-wrap',
		width: '80%',
		marginLeft: '10%',
		marginBottom: 2,
		fontSize: 18,
	},
	noteFooter: {
		color: '#666',
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
	},
	centerText: {
		width: '90%',
		height: '25%',
		marginLeft: '5%',
		marginTop: '4%',
	},
	center: {
		width: '90%',
		height: '25%',
		marginLeft: '5%',
		marginTop: '25%',
	},
	keyFrame: {
		backgroundColor: '#9e5cf6',
		color: '#fff',
		marginRight: '90%',
		marginLeft: '-7%',
		display: 'flex',
		flexDirection: 'row',
	},
	keyElement: {
		backgroundColor: '#9e5cf6',
		color: '#fff',
		borderRadius: 50,
		paddingRight: 20,
		fontSize: 18,
		fontWeight: 600,
	},
	keyDot: {
		backgroundColor: '#9e5cf6',
		color: '#9e5cf6',
		borderRadius: 50,
		fontSize: 18,
		marginLeft: -40
	},
	key: {
		backgroundColor: '#9e5cf6',
		color: '#fff',
		marginRight: '50%',
		marginLeft: '42%',
	},
});

export default Card
