import { useState, useEffect } from 'react'
import { nanoid } from 'nanoid'
import { MdHome } from 'react-icons/md'
import { MdAddCircle } from 'react-icons/md'
import NotesList from './NotesList'
import Search from './Search'
import Header from './Header'
import { StyleSheet, View, Text, SafeAreaView } from 'react-native'
import Axios from 'axios'


export default function MainPage({ navigation }) {

	const loadScene = () => {
		navigation.navigate('MenuPage');
	}

	const loadScene2 = () => {
		navigation.navigate('CreatePage');
	}

	const [notes, setNotes] = useState([
		{
			id: nanoid(),
			key: 'Зразок',
			text: 'Тут ключ',
			date: 'Місце для передавання ключа',
			today: '25/10/2023'
		},
	]);

	const [searchText, setSearchText] = useState('');

	useEffect(() => {
		const savedNotes = JSON.parse(
			localStorage.getItem('noteslist')
		);

		if (savedNotes) {
			setNotes(savedNotes);
		}
	}, []);

	useEffect(() => {
		localStorage.setItem(
			'noteslist',
			JSON.stringify(notes)
		);
	}, [notes]);

	const deleteNote = (id) => {
		const newNotes = notes.filter((note) => note.id !== id);
		setNotes(newNotes);

		function s1() {
			const session = localStorage.getItem('session')
			const noteslist = localStorage.getItem('noteslist')

			const values = {
			noteslist: noteslist,
			session: session,
			}

			console.log(values)
			Axios.put("http://localhost:19007/notes", values)
			}
			setTimeout(s1, 400)
	};

	return (
		<SafeAreaView>
			<View>
				<View style={styles.btns}>
					<View style={styles.middle}>
					<MdHome color='#9e5cf6'
						onClick={loadScene}
						size='2.1em'
					/>
						<Text style={styles.text}>В меню</Text>
					</View>
					<View style={styles.middle}>
					<MdAddCircle color='#9e5cf6'
						onClick={loadScene2}
						size='1.9em'
					/>
						<Text style={styles.text}>Додати</Text>
					</View>
				</View>

					<Header></Header>
					<Search handleSearchNote={setSearchText} />
					<NotesList
						notes={notes.filter((note) =>
							note.text.toLowerCase().includes(searchText)
						)}
						handleDeleteNote={deleteNote}
					/>
			</View>
				<View style={styles.middletxt}>
					<Text style={styles.txt}>
						app created by
					</Text>
					<Text style={styles.txtdef}>
						Tockyj Bohdan
					</Text>
				</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	middle: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
	},
	btns: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
		width: '90%',
		marginLeft: '4%',
		marginTop: 20,
	},
	text: {
		fontSize: 20,
		marginTop: -1,
		marginLeft: 1,
	},
	txt: {
		fontSize: 16,
		marginTop: 12,
		marginBottom: 4,
		color: '#888'
	},
	txtdef: {
		fontSize: 16,
		color: '#888'
	},
	middletxt: {
		alignItems: 'center',
	},
});
